mapper.select:
select *
from (select rownum r_num, a.*
	  from (select * from board order by groups desc, lev asc) a
) where r_num between #{begin} and #{end}

mapper.update:
update board
set writer=#{writer}, title=#{title}, filename=#{filename}, content=#{content}
where idx=#{idx}

update board
set   hit = hit + 1 
where idx= #{idx}
